import React, { useEffect , useState } from "react"
import ItemDetail from "./ItemDetail"
import { useParams } from "react-router-dom"



const productosIniciales = [
        {id: "1", titulo: "Camisa Mykonos", precio: 3500, Catalogo: "Mocca", descripcion: "Camisa denim azul, modelo oversize corta", PictureUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-eHenCshMXx0MjzSiD2HZQscr8b8gaVdHB1h_3JfdYnRrgWoIGhHFJRkOJjQg9Q9opk8&usqp=CAU",}, 
        {id: "2", titulo: "Blusa Santorini", precio: 3000, Catalogo:"Sale", descripcion: "Camisa denim azul, modelo oversize corta", PictureUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-eHenCshMXx0MjzSiD2HZQscr8b8gaVdHB1h_3JfdYnRrgWoIGhHFJRkOJjQg9Q9opk8&usqp=CAU",},
        {id: "3", titulo: "Camisa Rodas", precio: 2800, Catalogo: "Mocca", descripcion: "Camisa denim azul, modelo oversize corta", PictureUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-eHenCshMXx0MjzSiD2HZQscr8b8gaVdHB1h_3JfdYnRrgWoIGhHFJRkOJjQg9Q9opk8&usqp=CAU",},
        {id: "4", titulo: "Sobrecamisa Milos", precio: 5500, Catalogo:"Sale", descripcion: "Camisa denim azul, modelo oversize corta", PictureUrl: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-eHenCshMXx0MjzSiD2HZQscr8b8gaVdHB1h_3JfdYnRrgWoIGhHFJRkOJjQg9Q9opk8&usqp=CAU",}
    ]    

    const ItemDetailContainer = () => {
        
        const [productos, setProductos] = useState([])
        const { id }=useParams()
        
        useEffect( () => {
            
            const mock_async = new Promise((resolver) => {
                setTimeout (()=>{
                    resolver(productosIniciales)
            },2000)
            
        })
        mock_async
        .then(productos=> {
            const producto = productos.find(producto => producto.id===id);
            setProductos(producto)
        })
        
        
    })

    if(productos.length > 0){
        return (
            <>
            <div className="d-flex justify-content-center container mt-3 mb-3">
                {productos.map((producto, i)=>{
                    return <ItemDetail producto={producto}/>

                })}
            </div>
            </>
        );
            }else {
        return (
            <>
            <p>Cargando...</p>
            </>
        )
    }
    }
export default  ItemDetailContainer
