import React from "react"
import "./estilos.css"


const ItemCount = () => {
    const [valor, setValor] = useState(0);
    const handleSum = () => {
        setValor(valor + 1);
      };
    
      const handleSubstract = () => {
        if (valor === 0) {
          return;
        }
        setValor(valor - 1);
      };
    return (
        <div>
            <div className="card mt-5 ms-5" style={{width:"18rem"}}>
                <img src="https://enede.com.ar/shop/16035-large_default/camisa-yello.jpg" className="card-img-top" alt="..."/>
                    <div className="card-body">
                        <h5 className="card-title">Camisa Mykonos</h5>
                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        <a href="#" className="btn btn-secondary">Agregar!</a>
                        <div className="botones mt-3">
                        <a href="#" className="btn btn-dark" onClick={onSum}>+</a>
                        <span>0</span>
                        <a href="#" className="btn btn-dark" onClick={onSubstract}>-</a>
                        </div>
                    </div>
            </div>
        </div>
     )
}

export {ItemCount}
